mapper.select:
select shops.id as shopId, shops.name as shopName, users.name as shopUserName, shops.tax_register_no as shopTax, shop_extras.outer_code as shopCode, shop_extras.rate as shopRate, shop_extras.rate_updating as shopRateOne, shop_extras.deposit_need/100 as shopDeposit, shop_extras.tech_fee_need/100 as shopTechFee, nb_deposit_account.balance/100 as shopBalance, exper_users.loginName, exper_ience.experiencename, exper_ience.taxRegisterNo, exper_ience.experCode, exper_ience.id as experid, exper_shop.resourceCode as resocode, concat('http://home.nowbook.com',exper_ience.licence) as licence, concat('http://home.nowbook.com',exper_ience.certificate) as certificate, concat('http://home.nowbook.com',exper_ience.openinglicenses) as openinglicenses, concat('http://home.nowbook.com',exper_ience.Orgcertificate) as orgcertificate, concat('http://home.nowbook.com',exper_ience.frontlicense) as frontlicense, concat('http://home.nowbook.com',exper_ience.backlicense) as backlicense, concat('http://home.nowbook.com',exper_ience.ContractOne) as contractOne, concat('http://home.nowbook.com',exper_ience.ContractTwo) as contractTwo, exper_shop.status as status, exper_shop.checkStatus as checkstatus, case shops.business_id when 1 then '锦霖家电' when 4 then '家饰' when 2 then '家具' when 5 then '水家电' when 3 then '建材' end as businessName from exper_ience inner join exper_shop on exper_shop.experId = exper_ience.id inner join shops on exper_shop.shopId = shops.id inner join shop_extras on shops.id = shop_extras.shop_id inner join users on shops.user_id = users.id left join nb_deposit_account on shops.user_id = nb_deposit_account.seller_id inner join exper_users on exper_users.id = exper_ience.experUserId where 1=1
and shops.name=#{shopName}
and shops.user_name=#{sellerName}
