mapper.select:
select id,
from nb_coupons where
  startTime <= #{startTime} and endTime >= #{endTime}   
and status = #{status}

mapper.update:
UPDATE nb_coupons
SET status = #{status}
WHERE id = #{id}

mapper.select:
select count(1) from nb_coupons where 1=1
and channelId = #{businessId}
and cpName like concat(concat('%',#{cpName}),'%')
and  DATE_FORMAT(created_at,'%Y-%m-%d')>=DATE_FORMAT(#{beginCreatedAt},'%Y-%m-%d')
and DATE_FORMAT(created_at,'%Y-%m-%d')<=DATE_FORMAT(#{endCreatedAt},'%Y-%m-%d')
and status = #{status}
and couponsType = #{couponsType}
and sellerId = #{sellerId}
and seller_Name like concat(concat('%',#{sellerName}),'%')
and shop_Name like concat(concat('%',#{shopName}),'%')

select id,

from nb_coupons
where 1=1
and channelId = #{businessId}
and cpName like concat(concat('%',#{cpName}),'%')
and DATE_FORMAT(created_at,'%Y-%m-%d')>=DATE_FORMAT(#{beginCreatedAt},'%Y-%m-%d')  
and DATE_FORMAT(created_at,'%Y-%m-%d')<=DATE_FORMAT(#{endCreatedAt},'%Y-%m-%d')
and status = #{status}
and couponsType = #{couponsType}
and sellerId = #{sellerId}
and seller_Name like concat(concat('%',#{sellerName}),'%')
and shop_Name like concat(concat('%',#{shopName}),'%')
ORDER BY created_at DESC LIMIT #{offset}, #{limit}

select count(*) from nb_coupons where sellerId=#{sellerId} and couponsType = 2
 and status =0
 and created_at>=#{startTime}
 and created_at<=#{endTime}
 and cpName=#{cpName}
 and status=#{status}
order by nb_coupons.created_at desc

SELECT * FROM nb_coupons rc WHERE  (rc.status = 1 or rc.status = 2) and rc.couponsType = 2 and rc.id IN (SELECT DISTINCT rcil.couponsId FROM nb_coupons_item_list  rcil WHERE rcil.couponsId IN (SELECT DISTINCT couponId FROM nb_coupons_users WHERE userId = #{userId} AND STATUS = #{userStatus} )

    AND rcil.itemId IN (#{itemIds})

)
 and   rc.startTime <= #{startTime} and rc.endTime >= #{endTime}
