mapper.update:
update freight_models
model_name=#{modelName}, cost_way=#{costWay}, first_amount=#{firstAmount}, first_fee=#{firstFee}, add_amount=#{addAmount}, add_fee=#{addFee}, status=#{status}, special_exist=#{specialExist}, updated_at = now()
where id=#{id}

mapper.select:
select id, 
from freight_models

    seller_id=#{sellerId}
    and status=1

        and model_name like CONCAT('%', #{modelName} ,'%')

     and count_way=#{countWay} 
     and cost_way=#{costWay} 

limit #{offset}, #{size};

select count(1) from freight_models

    seller_id=#{sellerId}
    and status=1

        and model_name like CONCAT('%', #{modelName} ,'%')

     and count_way=#{countWay} 
     and cost_way=#{costWay}

select id, 
from freight_models

    seller_id=#{sellerId} and model_name=#{modelName}
     and id!=#{modelId} 
    and status=1
